CREATE TABLE hotel (hotel_id INTEGER PRIMARY KEY NOT NULL, hotel_name VARCHAR (50) NOT NULL, pool VARCHAR (5) NOT NULL, kids_club VARCHAR (5) NOT NULL, entertainment VARCHAR (5) NOT NULL, restaurant VARCHAR (5) NOT NULL, distance_beach INT (5) NOT NULL, distance_downtown INT (5) NOT NULL, price_extra_bed INT (5) NOT NULL, price_half_board INT (5), price_full_board INT (5), review INT (2) NOT NULL);

