CREATE TABLE meals (booking_id REFERENCES booking (booking_id) NOT NULL, room_id REFERENCES room (room_id) NOT NULL, meals VARCHAR (10));

-- Table: party
CREATE TABLE party (customer_id INTEGER REFERENCES customer (customer_id) ON DELETE CASCADE NOT NULL, booking_id INT REFERENCES booking (booking_id) NOT NULL, room_id INT REFERENCES room (room_id) NOT NULL);

-- Table: room
CREATE TABLE room (room_id INTEGER PRIMARY KEY NOT NULL, hotel_id INT (10) NOT NULL REFERENCES hotel (hotel_id), beds INT (10) NOT NULL, price INT (10) NOT NULL);

-- View: avail_date
CREATE VIEW avail_date AS SELECT room.room_id, booking.booking_id, booking.check_in_date, booking.check_out_date
FROM booking
INNER JOIN party ON booking.booking_id = party.booking_id 
INNER JOIN room ON party.room_id = room.room_id
WHERE NOT (('2022-07-01' <= check_out_date AND '2022-07-01' >= check_in_date) 
    OR ('2022-07-08' <= check_out_date AND '2022-07-08' >= check_in_date))
GROUP BY room.room_id;

-- View: avail_room_pool
CREATE VIEW avail_room_pool AS SELECT room.room_id AS 'Room_Number', 
hotel.hotel_name AS 'Hotel name', 
hotel.review AS 'Rating',
hotel.distance_downtown AS 'Distance_centrum',
hotel.distance_beach AS 'Distance_beach',
room.beds AS 'Room_beds',
room.price AS 'Price_per_night',
booking.check_in_date AS 'Check_in_date',
booking.check_out_date AS 'Check_out_date',
hotel.restaurant AS 'Restaurant',
hotel.kids_club AS 'Kids_club',
hotel.pool AS 'Pool',
hotel.entertainment AS 'Entertainment' 
FROM hotel, booking
LEFT JOIN room ON hotel.hotel_id = room.hotel_id
